CREATE TABLE mainUser ( userId INT PRIMARY KEY AUTO_INCREMENT, credentialNumber VARCHAR(40) NOT NULL UNIQUE, nameUser VARCHAR(50) NOT NULL, lastnameUser VARCHAR(60) NOT NULL, credentialType VARCHAR(2) NOT NULL, addressUser VARCHAR(60) NOT NULL, dob DATE NOT NULL, passwordHash VARBINARY(255) NOT NULL, saltPassword VARBINARY(255) NOT NULL, userStatus BIT NOT NULL DEFAULT 1, phoneNumber text NOT null, UserEmail text NOT null);